import Header from "../components/organism/Header";
import { useState } from "react";

function Preferences() {
    const [darkMode, setDarkMode] = useState(false);

    const handleToggle = () => {
        setDarkMode(!darkMode);
        // You can add logic here to apply dark mode styles globally or within the app.
    };

    return (
        <div className={`min-h-screen ${darkMode ? "bg-gray-800 text-white" : "bg-gray-100 text-gray-900"}`}>
            <Header className="w-full" />
            <div className="flex justify-center py-10">
                <div className="w-full max-w-md p-6 bg-white shadow-md rounded-lg border border-gray-200">
                    <h2 className="text-2xl font-semibold mb-4 text-center">Preferences</h2>

                    <div className="flex items-center justify-between py-3 border-b border-gray-200">
                        <span className="text-lg">Dark Mode</span>
                        <label className="relative inline-flex items-center cursor-pointer">
                            <input
                                type="checkbox"
                                className="sr-only peer"
                                checked={darkMode}
                                onChange={handleToggle}
                            />
                            <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-2 peer-focus:ring-blue-500 dark:bg-gray-700 rounded-full peer dark:peer-focus:ring-blue-800 peer-checked:after:translate-x-5 peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600"></div>
                        </label>
                    </div>

                    {/* Additional preferences can be added here */}
                    <div className="flex items-center justify-between py-3">
                        <span className="text-lg">Other Setting</span>
                        <button className="text-blue-500 hover:text-blue-700">Configure</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Preferences;
