import React from "react";
import Button from './atom/Button';
import ItemSmallCard from "./molecule/ItemSmallCard";
import {useState} from "react";


const Basket = ({basketItems: initialBasketItems }) => {

    const [basketItems, setBasketItems] = useState([]);

    const total = basketItems.reduce((acc, basketItem) => acc + basketItem.item.price * basketItem.count, 0);

    const removeFromBasket = (itemToRemove) => {
        setBasketItems(basketItems.filter(basketItem => basketItem.item.id !== itemToRemove.id));
    };

    return (
        <div className="bg-secondary p-4 rounded-lg overflow-auto max-h-screen">
            {basketItems.map((basketItem, index) => (
                <ItemSmallCard item={basketItem.item} count={basketItem.count} key={index} removeFromBasket={removeFromBasket}/>
            ))}
            <div className="my-4 bottom-3 flex justify-center">
                <Button text={`Total: ${total}€`} bgColor="primary" textColor="white" />
            </div>
        </div>
    );
};

export default Basket;
