import {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import Button from "../atom/Button";

function UpdateItem (){

    let {idArticle} = useParams();

    const [itemDetail, setItemDetail] = useState({})

    useEffect(() => {
        fetch(`http://localhost:3000/${idArticle}`)
            .then((response) => response.json())
            .then((data) => {
                console.log(data)
                setItemDetail(data)
            })
            .catch((error) => {
                console.error('Erreur récupération du détail de la plante:', error);
            })
    }, [idArticle])

    useEffect(() => {
        fetch(`http://localhost:3000/${idArticle}`)
            .then((response) => response.json())
            .then((data) => {
                setItemDetail(data)
                setUpdatedItem(data);
            })
            .catch((error) => {
                console.error('Erreur récupération du détail de la plante:', error);
            })
    }, [idArticle])

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedItem((prevItem) => ({
            ...prevItem, [name]: value
        }));
    };

    const handleUpdatePlant = () => {
        fetch(`http://localhost:3000/${idArticle}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json'},
            body: JSON.stringify(updatedItem)
        })
            .then(response => response.json())
            .then(data => {
                setItemDetail(data); // MAJ l'état avec les nouvelles données
                setIsEditing(false);
            })
            .catch(error => console.error('Erreur lors de la requête PUT :', error)); };

    const [isEditing, setIsEditing] = useState(false);
    const [updatedItem, setUpdatedItem] = useState({
        name: '',
        cover: '',
        price: 0,
        comfort: 0,
        size: ''
    });

    return (
        <div className="flex flex-col lg:flex-row items-center lg:items-start p-8 bg-gray-50 rounded-lg shadow-md max-w-4xl mx-auto mt-8">
            {!isEditing ? (
                <div >
                    <h2>id Article {idArticle}</h2>
                    <h2>{itemDetail.name}</h2>
                    <h2>Prix : {itemDetail.price} euros</h2>
                    <h2>Confort : {itemDetail.comfort}</h2>
                    <h2>Taille : {itemDetail.size}</h2>
                    <Button onClick={() => setIsEditing(true)} bgColor={"primary"} textColor={"white"} text={"Modifier"}/>
                </div>
            ) : (
                <div >
                    <label>Nom:</label>
                    <input type="text" name="name"
                           value={updatedItem.name} onChange={handleInputChange} />
                    <Button onClick={handleUpdatePlant} bgColor={"primary"} textColor={"white"} text={"Enregistrer"}/>
                    <Button onClick={() => setIsEditing(false)} bgColor={"lightGrey"} textColor={"black"} text={"Annuler"}/>
                </div>
            )}
        </div>
    )

} export default UpdateItem;
