import {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import Button from "../atom/Button";
import {InputDefault} from "../atom/InputDefault";

function UpdateItem (){

    let {idArticle} = useParams();

    const [itemDetail, setItemDetail] = useState({})

    useEffect(() => {
        fetch(`http://localhost:3000/${idArticle}`)
            .then((response) => response.json())
            .then((data) => {
                console.log(data)
                setItemDetail(data)
            })
            .catch((error) => {
                console.error('Erreur récupération du détail de la plante:', error);
            })
    }, [idArticle])

    useEffect(() => {
        fetch(`http://localhost:3000/${idArticle}`)
            .then((response) => response.json())
            .then((data) => {
                setItemDetail(data)
                setUpdatedItem(data);
            })
            .catch((error) => {
                console.error('Erreur récupération du détail de la plante:', error);
            })
    }, [idArticle])

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedItem((prevItem) => ({
            ...prevItem, [name]: value
        }));
    };

    const handleUpdatePlant = () => {
        fetch(`http://localhost:3000/${idArticle}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json'},
            body: JSON.stringify(updatedItem)
        })
            .then(response => response.json())
            .then(data => {
                setItemDetail(data); // MAJ l'état avec les nouvelles données
                setIsEditing(false);
            })
            .catch(error => console.error('Erreur lors de la requête PUT :', error)); };

    const [isEditing, setIsEditing] = useState(false);
    const [updatedItem, setUpdatedItem] = useState({
        name: '',
        cover: '',
        price: 0,
        comfort: 0,
        size: ''
    });

    return (
        <div className="flex flex-col lg:flex-row items-center lg:items-start p-8 bg-gray-50 rounded-lg shadow-md max-w-4xl mx-auto mt-8">
            {!isEditing ? (
                <div className="justify-center">
                    <Button onClick={() => setIsEditing(true)} bgColor={"primary"} textColor={"white"} text={"Modifier le produit"}/>
                </div>
            ) : (
                <div>
                    <div className="w-full mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2">
                            Nom :
                        </label>
                        <input
                            name="name"
                            type="text"
                            value={updatedItem.name}
                            onChange={handleInputChange}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        />
                    </div>
                    <label>Nom:</label>
                    <input type="text" name="name"
                           value={updatedItem.name} onChange={handleInputChange}/>
                    {/*<InputDefault type={"text"} name="name" value={updatedItem.name} method={handleInputChange} label={"Nom"}/>*/}
                    <Button onClick={handleUpdatePlant} bgColor={"primary"} textColor={"white"} text={"Enregistrer"}/>
                    <Button onClick={() => setIsEditing(false)} bgColor={"lightGrey"} textColor={"black"}
                            text={"Annuler"}/>
                </div>
            )}
        </div>
    )

}

export default UpdateItem;
