import React, { useEffect, useState } from "react";
import Button from "../components/atom/Button";
import Header from "../components/organism/Header";
import { useHistory } from 'react-router-dom';

const Payment = () => {
    const [basketItems, setBasketItems] = useState([]);

    const [userDetails, setUserDetails] = useState({
        fullName: '',
        address: '',
        email: '',
        city: '',
        postalCode: ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserDetails({
            ...userDetails,
            [name]: value
        });
    };

    const history = useHistory();
    const handlePayment = (e) => {
        e.preventDefault();
        // vider le panier
        setBasketItems([]);
        localStorage.setItem('basketItems', JSON.stringify([])); // Clear local storage
        history.push('/confirmation');
    };

    useEffect(() => {
        const items = JSON.parse(localStorage.getItem('basketItems'));
        if (items) {
            setBasketItems(items);
        } else {
            setBasketItems([]); // Ensure basketItems is at least an empty array
        }
    }, []);

    const total = basketItems.reduce((acc, basketItem) => acc + (basketItem.item.onSale ? basketItem.item.price * (1 - basketItem.item.discount) : basketItem.item.price) * basketItem.count, 0);

    return (
        <div>
            <Header />
            <div className="container mx-auto p-8">
                <h1 className="text-2xl font-bold mb-4">RÃ©capitulatif de votre commande</h1>

                {/* Liste des articles dans le panier */}
                <div className="bg-white p-6 rounded-lg shadow-md mb-4">
                    {basketItems.length === 0 ? (
                        <p className="text-gray-700">Votre panier est vide</p>
                    ) : (
                        basketItems.map((basketItem, index) => (
                            <div
                                key={index}
                                className="flex justify-between items-center border-b border-gray-200 py-4"
                            >
                                <div className="flex items-center">
                                    <img
                                        src={basketItem.item.cover}
                                        alt={basketItem.item.name}
                                        className="w-16 h-16 object-cover rounded mr-4"
                                    />
                                    <div>
                                        <h2 className="font-bold text-lg">{basketItem.item.name}</h2>
                                        <p className="text-gray-700">QuantitÃ©: {basketItem.count}</p>
                                    </div>
                                </div>
                                <div className="flex-end">
                                    <p className={basketItem.item.onSale ? "line-through" : "font-bold"}>
                                        {basketItem.item.price}â‚¬
                                    </p>
                                    <p className="font-bold">
                                        {basketItem.item.onSale ? (basketItem.item.price * (1 - basketItem.item.discount)).toFixed(2) + "â‚¬" : ""}
                                    </p>
                                </div>
                            </div>
                        ))
                    )}
                </div>

                {/* Total et bouton de paiement */}
                <div className="flex justify-between items-center mb-8">
                    <h2 className="text-xl font-bold">Total: {total}â‚¬</h2>
                    <Button text="Payer maintenant" bgColor="primary" textColor="white" onClick={handlePayment} />
                </div>

                {/* Formulaire de dÃ©tails de l'utilisateur */}
                <div className="bg-white p-6 mt-8 rounded-lg shadow-md">
                    <h2 className="text-xl font-bold mb-4">Informations de livraison</h2>
                    <form onSubmit={handlePayment}>
                        {/* Nom complet */}
                        <div className="mb-4">
                            <label className="block text-gray-700 mb-2">Nom complet</label>
                            <input
                                type="text"
                                name="fullName"
                                value={userDetails.fullName}
                                onChange={handleInputChange}
                                className="w-full p-2 border border-gray-300 rounded-lg"
                                placeholder="Votre nom complet"
                                required
                            />
                        </div>

                        {/* Adresse */}
                        <div className="mb-4">
                            <label className="block text-gray-700 mb-2">Adresse</label>
                            <input
                                type="text"
                                name="address"
                                value={userDetails.address}
                                onChange={handleInputChange}
                                className="w-full p-2 border border-gray-300 rounded-lg"
                                placeholder="Votre adresse"
                                required
                            />
                        </div>

                        {/* Ville et Code postal */}
                        <div className="flex justify-between mb-4">
                            <div className="w-1/2 mr-2">
                                <label className="block text-gray-700 mb-2">Ville</label>
                                <input
                                    type="text"
                                    name="city"
                                    value={userDetails.city}
                                    onChange={handleInputChange}
                                    className="w-full p-2 border border-gray-300 rounded-lg"
                                    placeholder="Ville"
                                    required
                                />
                            </div>
                            <div className="w-1/2 ml-2">
                                <label className="block text-gray-700 mb-2">Code postal</label>
                                <input
                                    type="text"
                                    name="postalCode"
                                    value={userDetails.postalCode}
                                    onChange={handleInputChange}
                                    className="w-full p-2 border border-gray-300 rounded-lg"
                                    placeholder="Code postal"
                                    required
                                />
                            </div>
                        </div>

                        {/* Email */}
                        <div className="mb-4">
                            <label className="block text-gray-700 mb-2">Email</label>
                            <input
                                type="email"
                                name="email"
                                value={userDetails.email}
                                onChange={handleInputChange}
                                className="w-full p-2 border border-gray-300 rounded-lg"
                                placeholder="Votre email"
                                required
                            />
                        </div>

                        {/* Formulaire de paiement */}
                        <div className="bg-white p-6 mt-8 rounded-lg shadow-md">
                            <h2 className="text-xl font-bold mb-4">Informations de paiement</h2>
                            <div className="mb-4">
                                <label className="block text-gray-700 mb-2">Nom sur la carte</label>
                                <input
                                    type="text"
                                    className="w-full p-2 border border-gray-300 rounded-lg"
                                    placeholder="Nom complet"
                                    required
                                />
                            </div>
                            <div className="mb-4">
                                <label className="block text-gray-700 mb-2">NumÃ©ro de carte</label>
                                <input
                                    type="text"
                                    className="w-full p-2 border border-gray-300 rounded-lg"
                                    placeholder="1234 5678 9101 1121"
                                    required
                                />
                            </div>
                            <div className="flex justify-between mb-4">
                                <div className="w-1/2 mr-2">
                                    <label className="block text-gray-700 mb-2">Date d'expiration</label>
                                    <input
                                        type="text"
                                        className="w-full p-2 border border-gray-300 rounded-lg"
                                        placeholder="MM/AA"
                                        required
                                    />
                                </div>
                                <div className="w-1/2 ml-2">
                                    <label className="block text-gray-700 mb-2">CVV</label>
                                    <input
                                        type="text"
                                        className="w-full p-2 border border-gray-300 rounded-lg"
                                        placeholder="123"
                                        required
                                    />
                                </div>
                            </div>
                            <Button text="Confirmer le paiement" bgColor="primary" textColor="white" onClick={handlePayment} />
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Payment;
