import React, { useState } from "react";

const SearchBar = ({ onSearch }) => {
    const [searchQuery, setSearchQuery] = useState("");
    const [category, setCategory] = useState("");
    const [minPrice, setMinPrice] = useState("");
    const [maxPrice, setMaxPrice] = useState("");
    const [availableOnly, setAvailableOnly] = useState(false);

    const handleSearch = (e) => {
        e.preventDefault();
        onSearch({
            searchQuery,
            category,
            minPrice,
            maxPrice,
            availableOnly
        });
    };

    return (
        <form onSubmit={handleSearch} className="p-4 bg-white shadow-md rounded-lg">
            {/* Search by Plant Name */}
            <div className="mb-4">
                <label className="block text-gray-700 mb-2">Nom de la plante</label>
                <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded-lg"
                    placeholder="Rechercher une plante"
                />
            </div>

            {/* Category Filter */}
            <div className="mb-4">
                <label className="block text-gray-700 mb-2">Catégorie</label>
                <select
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded-lg"
                >
                    <option value="">Toutes les catégories</option>
                    <option value="Indoor">Plante d'intérieur</option>
                    <option value="Outdoor">Plante d'extérieur</option>
                </select>
            </div>

            {/* Price Range Filter */}
            <div className="mb-4">
                <label className="block text-gray-700 mb-2">Prix</label>
                <div className="flex">
                    <input
                        type="number"
                        value={minPrice}
                        onChange={(e) => setMinPrice(e.target.value)}
                        className="w-1/2 p-2 border border-gray-300 rounded-lg mr-2"
                        placeholder="Prix min"
                    />
                    <input
                        type="number"
                        value={maxPrice}
                        onChange={(e) => setMaxPrice(e.target.value)}
                        className="w-1/2 p-2 border border-gray-300 rounded-lg"
                        placeholder="Prix max"
                    />
                </div>
            </div>

            {/*/!* Availability Filter *!/*/}
            {/*<div className="mb-4">*/}
            {/*    <label className="block text-gray-700 mb-2">*/}
            {/*        <input*/}
            {/*            type="checkbox"*/}
            {/*            checked={availableOnly}*/}
            {/*            onChange={() => setAvailableOnly(!availableOnly)}*/}
            {/*            className="mr-2"*/}
            {/*        />*/}
            {/*        Disponible uniquement*/}
            {/*    </label>*/}
            {/*</div>*/}

            {/* Search Button */}
            <button type="submit" className="w-full bg-primary text-white p-2 rounded-lg">
                Rechercher
            </button>
        </form>
    );
};

export default SearchBar;
