import React from "react";
import Button from "../atom/Button";

function ItemCard({ item, addToBasket }) {


    const price = item.onSale ? item.price * (1-item.discount) : item.price;

    return (
        <div className={`m-4 ${darkMode ? "bg-darkGrey" : "bg-white"} shadow-lg rounded-lg overflow-hidden max-w-xs relative transform hover:scale-105
        transition-transform duration-200`}>
            <a href={`/detail/${item.id}`}>
                <div className={`text-white text-xs font-bold uppercase ${item.onSale ? 'bg-red' : 'bg-white'}`}>
                    {item.onSale ? 'Promo !' : '-'}
                </div>

                <div className="p-4">
                    <h2 className="font-bold text-lg mb-2">{item.name}</h2>
                    <div className="overflow-hidden">
                        <img src={item.cover} alt={item.name}
                             className="h-48 w-full object-cover mt-2"/>
                    </div>
                    <p className="text-gray-700">{item.size}</p>
                    <div className={"flex flex-row gap-4"}>
                        <p className="font-bold">{item.onSale ? price.toFixed(2) + "€" : ""}</p>
                        <p className={item.onSale ? "line-through" : "font-bold"}>{item.price}€</p>
                    </div>
                </div>
            </a>
            <div className=" flex justify-center pb-2">
                <Button text="Ajouter au panier" bgColor="primary" textColor="white"
                        onClick={() => addToBasket(item)}/>
            </div>
        </div>
    );
}

export default ItemCard;
