import React from "react";

function ItemCard({ item }) {
    return (
        <div className="m-4 bg-white shadow-lg rounded-lg overflow-hidden max-w-xs relative">
            {item.onSale && (
                <div className="absolute top-0 right-0 bg-red-500 text-white px-2 py-1 text-xs font-bold uppercase">En solde</div>
            )}
            <div className="p-4">
                <h2 className="font-bold text-lg mb-2">{item.name}</h2>
                <div className="overflow-hidden">
                    <img src={item.cover} alt={item.name}
                         className="h-48 w-full object-cover mt-2 transform hover:scale-110 transition-transform duration-200"/>
                </div>
                <p className="text-gray-700">{item.size}</p>
                <p className="font-bold">{item.price}â‚¬</p>
            </div>
        </div>
    );
}

export default ItemCard;
