import React from "react";
import {Trash2} from "lucide-react";
import ButtonIcon from '../atom/ButtonIcon';

function ItemSmallCard({ item, count, removeFromBasket }) {
    return (
        <div className="mb-4 bg-white shadow-lg p-4 rounded-lg flex items-center justify-between transform hover:scale-105 transition-transform duration-200">
            <div className="flex items-center">
                <img src={item.cover} alt={item.name} className="w-16 h-16 mr-4 rounded-lg "/>
                <div>
                    <h2 className="text-lg font-semibold">{item.name}</h2>
                    <p>Prix: {item.onSale ? item.price*(1-item.discount) : item.price}€</p>
                    <p>Quantité: {count}</p>
                </div>
            </div>
            <div className="">
                <ButtonIcon icon={<Trash2 color="#4d4d4d"/>} bgColor="lightGrey" onClick={() => removeFromBasket(item)}/>
            </div>
        </div>
    );
}

export default ItemSmallCard;
