import Button from './atom/Button';
import {itemList} from "../datas/ItemList";

const Basket = () => {
    const total = itemList.reduce((acc, item) => acc + item.price, 0);

    return (
        <div className="bg-secondary p-4 rounded-lg">
            {itemList.map((item, index) => (
                <div key={index} className="mb-4 border-b border-gray-300 pb-2 flex items-center">
                    <img src={item.cover} alt={item.name} className="w-16 h-16 mr-4" />
                    <div>
                        <h2 className="text-lg font-semibold">{item.name}</h2>
                        <p>Catégorie: {item.category}</p>
                        <p>Besoins en lumière: {item.lightRequirement}</p>
                        <p>Taille: {item.size}</p>
                        {item.onSale && <span className="text-red-500">En promotion !</span>}
                        <p>Prix: {item.price}€</p>
                    </div>
                </div>
            ))}
            <div className="mt-4">
                <h3 className="text-xl font-bold">Total: {total}€</h3>
                <Button text="Procéder au paiement" bgColor="primary" textColor="white" />
            </div>
        </div>
    );
};

export default Basket;
