import React from "react";
import Button from "../atom/Button";

function ItemCard({ item, addToBasket }) {

    const price = item.onSale ? item.price * 0.8 : item.price;
    
    return (
        <div className="m-4 bg-white shadow-lg rounded-lg overflow-hidden max-w-xs relative transform hover:scale-105 transition-transform duration-200">
            <div className={`text-white text-xs font-bold uppercase ${item.onSale ? 'bg-red' : 'bg-black'}`}>
                {item.onSale ? 'En solde' : 'Aucune promo'}
            </div>

            <div className="p-4">
                <h2 className="font-bold text-lg mb-2">{item.name}</h2>
                <div className="overflow-hidden">
                    <img src={item.cover} alt={item.name}
                         className="h-48 w-full object-cover mt-2"/>
                </div>
                <p className="text-gray-700">{item.size}</p>
                <p className="font-bold">{item.price}â‚¬</p>
            </div>
            <div className=" flex justify-center pb-2">
                <Button text="Ajouter au panier" bgColor="primary" textColor="white" onClick={() => addToBasket(item)}/>
            </div>
        </div>
    );
}

export default ItemCard;
