import Button from './atom/Button';
import {itemList} from "../datas/ItemList";

const Basket = () => {
    // Sélection de deux plantes de la liste
    const selectedItems = itemList.filter(item => item.name === 'Ficus Lyrata' || item.name === 'Monstera Deliciosa');

    // Calcul du total des deux plantes sélectionnées
    const total = selectedItems.reduce((acc, item) => acc + item.price, 0);

    return (
        <div className="bg-secondary p-4 rounded-lg">
            {selectedItems.map((item, index) => (
                <div
                    key={index}
                    className="mb-4 bg-gray-100 border border-gray-300 p-4 rounded-lg flex items-center"
                >
                    <img src={item.cover} alt={item.name} className="w-16 h-16 mr-4 rounded-lg" />
                    <div>
                        <h2 className="text-lg font-semibold">{item.name}</h2>
                        <p>Prix: {item.price}€</p>
                    </div>
                </div>
            ))}
            <div className="mt-4">
                <h3 className="text-xl font-bold">Total des plantes choisies: {total}€</h3>
                <Button text={`Total: ${total}€`} bgColor="primary" textColor="white" />
            </div>
        </div>
    );
};

export default Basket;
