import Header from '../components/organism/Header'
import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import Item from "../components/organism/Item";


function Detail() {

    let {idArticle} = useParams();

    const [itemDetail, setItemDetail] = useState({})
    useEffect(() => {
        fetch(`http://localhost:3000/${idArticle}`)
            .then((response) => response.json())
            .then((data) => {
                console.log(data)
                setItemDetail(data)
            })
            .catch((error) => {
                console.error('Erreur récupération du détail de la plante:', error);
            })
    }, [idArticle])

    const [isEditing, setIsEditing] = useState(false);
    const [updatedItem, setUpdatedItem] = useState({
        name: ''
        ,
        cover: ''
        ,
        price: 0,
        comfort: 0,
        size: ''
    });

    useEffect(() => {
        fetch(`http://localhost:3000/${idArticle}`)
            .then((response) => response.json())
            .then((data) => {
                setItemDetail(data)
                setUpdatedItem(data);
            })
            .catch((error) => {
                console.error('Erreur récupération du détail vêtement:', error);
            })
    }, [idArticle])

    return (
        <div>
            <Header className="w-full"/>
            <Item item={itemDetail}/>
            {!isEditing ? (
                <div >
                    <h2>id Article {idArticle}</h2>
                    <h2>{itemDetail.name}</h2>
                    <img
                        src={itemDetail.cover}
                        alt={`${itemDetail.name} cover`}
                    />
                    <h2>Prix : {itemDetail.price} euros</h2>
                    <h2>Confort : {itemDetail.comfort}</h2>
                    <h2>Taille : {itemDetail.size}</h2>
                    <button onClick={() => setIsEditing(true)}>Modifier</button>
                </div>
            ) : (
                <div >
                    <label>Nom:</label>
                    <input type="text" name="name"
                           value={updatedItem.name} onChange={handleInputChange} />
                    <button onClick={handleUpdateVetement}>Enregistrer</button>
                    <button onClick={() => setIsEditing(false)}>Annuler</button>
                </div> )}

        </div>
    )
}

export default Detail;
