import React from "react";

function ItemSmallCard({ item, count }) {
    return (
        <div className="mb-4 bg-white shadow-lg p-4 rounded-lg flex items-center transform hover:scale-105 transition-transform duration-200">
            <img src={item.cover} alt={item.name} className="w-16 h-16 mr-4 rounded-lg "/>
            <div>
                <h2 className="text-lg font-semibold">{item.name}</h2>
                <p>Prix: {item.price}€</p>
                <p>Quantité: {count}</p>
            </div>
        </div>
    );
}

export default ItemSmallCard;
