function Item ({ item }) {
  return (
      <div
          className="flex flex-col lg:flex-row items-center lg:items-start p-8 bg-gray-50 rounded-lg shadow-md max-w-4xl mx-auto mt-8">
          {/* Image Section */}
          <div className="w-full lg:w-1/2 mb-6 lg:mb-0">
              <img
                  src={item.cover}
                  alt={item.name}
                  className="w-full h-auto rounded-lg transform transition-transform duration-300 hover:scale-105"
              />
          </div>

          {/* Details Section */}
          <div className="w-full lg:w-1/2 lg:pl-8 text-left">
              <h1 className="text-3xl font-bold mb-4">{item.name}</h1>
              <p className="text-gray-700 mb-4">{item.description}</p>
              <p className="text-gray-600 mb-4">Taille: {item.size}</p>

              <div className="flex flex-row items-center gap-4">
                  {item.onSale && (
                      <p className="text-2xl font-bold text-red-600">
                          {(item.price * (1 - item.discount)).toFixed(2)}€
                      </p>
                  )}
                  <p className={item.onSale ? "line-through text-xl font-bold text-gray-500" : "text-2xl font-bold"}>
                      {item.price}€
                  </p>
              </div>
          </div>
      </div>
  )
}

export default Item;
