import React, {useContext, useEffect, useState} from "react";
import Header from "../components/organism/Header";

function History() {
    const [orders, setOrders] = useState([]);


    useEffect(() => {
        const fetchOrders = async () => {
                await fetch('http://localhost:3000/orders/')
                    .then(response => response.json())
                    .then(data => {
                        console.log(data)
                        setOrders(data)
                    })
                    .catch((error) =>  {
                        console.error('Erreur lors de la récupération des commandes:', error);
                    });
        };

        fetchOrders();
    }, []);


    return(
        <div>
            <Header/>
            <h1>Historique des commandes</h1>
            <div className="container mx-auto p-8">
                <h1 className="text-2xl font-bold mb-4">Historique des commandes</h1>

                {orders.length === 0 ? (
                    <p className="text-gray-700">Aucune commande passée.</p>
                ) : (
                    orders.map(order => (
                        <div key={order.orderId} className="bg-white p-6 rounded-lg shadow-md mb-4">
                            <h2 className="text-xl font-bold">Commande #{order.orderId}</h2>
                            <p>Date: {new Date(order.orderDate).toLocaleDateString()}</p>
                            <p>Total: {order.totalPrice}€</p>

                            <h3 className="text-lg font-semibold mt-4">Plantes commandées:</h3>
                            <ul>
                                {order.plants.map(plant => (
                                    <li key={plant.plantId} className="flex justify-between items-center py-2 border-b">
                                        <div className="flex items-center">
                                            <img
                                                src={plant.plantCover}
                                                alt={plant.plantName}
                                                className="w-16 h-16 object-cover rounded mr-4"
                                            />
                                            <div>
                                                <h4 className="font-bold">{plant.plantName}</h4>
                                                <p>Quantité: {plant.quantity}</p>
                                            </div>
                                        </div>
                                        <div>
                                            <p>Prix unitaire: {plant.price}€</p>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    ))
                )}
            </div>
        </div>
    )
}

export default History;
