import {useEffect, useState} from "react";
import {useParams} from "react-router-dom";

function UpdateItem (setItemDetail, itemDetail){

    let {idArticle} = useParams();

    useEffect(() => {
        fetch(`http://localhost:3000/${idArticle}`)
            .then((response) => response.json())
            .then((data) => {
                setItemDetail(data)
                setUpdatedItem(data);
            })
            .catch((error) => {
                console.error('Erreur récupération du détail de la plante:', error);
            })
    }, [idArticle])

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedItem((prevItem) => ({
            ...prevItem, [name]: value
        }));
    };

    const handleUpdatePlant = () => {
        fetch(`http://localhost:3000/${idArticle}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json'},
            body: JSON.stringify(updatedItem)
        })
            .then(response => response.json())
            .then(data => {
                setItemDetail(data); // MAJ l'état avec les nouvelles données
                setIsEditing(false);
            })
            .catch(error => console.error('Erreur lors de la requête PUT :', error)); };

    const [isEditing, setIsEditing] = useState(false);
    const [updatedItem, setUpdatedItem] = useState({
        name: '',
        cover: '',
        price: 0,
        comfort: 0,
        size: ''
    });

    return (
        <div>
            {!isEditing ? (
                <div >
                    <h2>id Article {idArticle}</h2>
                    <h2>{itemDetail.name}</h2>
                    <img
                        src={itemDetail.cover}
                        alt={`${itemDetail.name} cover`}
                    />
                    <h2>Prix : {itemDetail.price} euros</h2>
                    <h2>Confort : {itemDetail.comfort}</h2>
                    <h2>Taille : {itemDetail.size}</h2>
                    <button onClick={() => setIsEditing(true)}>Modifier</button>
                </div>
            ) : (
                <div >
                    <label>Nom:</label>
                    <input type="text" name="name"
                           value={updatedItem.name} onChange={handleInputChange} />
                    <button onClick={handleUpdatePlant}>Enregistrer</button>
                    <button onClick={() => setIsEditing(false)}>Annuler</button>
                </div>
            )}
        </div>
    )

} export default UpdateItem;
